    private void opPorcentajeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_opPorcentajeActionPerformed
        // TODO add your handling code here:
        if (pantalla.getText().length() > 0) {
            double num= Double.parseDouble(pantalla.getText());
            double resultado = num/100;
            pantalla.setText(String.valueOf(resultado));
            memoria.setText(String.valueOf(resultado));
        }
    }//GEN-LAST:event_opPorcentajeActionPerformed

