    private void opcomaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_opcomaActionPerformed
        // TODO add your handling code here:
        if (pantalla.getText().length() > 0 && cuentaComas(pantalla.getText())) {
            pantalla.setText(pantalla.getText() + ".");
        }
    }//GEN-LAST:event_opcomaActionPerformed

    private void opIgualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_opIgualActionPerformed
        // TODO add your handling code here:
        if(memoria.getText().length() > 0){
            double num1, num2, result;
            switch(memoria.getText().charAt(memoria.getText().length() - 1)){
                
                case '+':
                    num1 = Double.parseDouble(memoria.getText().substring(0, memoria.getText().length() - 1));
                    num2 = Double.parseDouble(pantalla.getText());
                    result = num1 + num2;
                    pantalla.setText(String.valueOf(result));
                    break;
                case '-':
                    num1 = Double.parseDouble(memoria.getText().substring(0, memoria.getText().length() - 1));
                    num2 = Double.parseDouble(pantalla.getText());
                    result = num1 - num2;
                    pantalla.setText(String.valueOf(result));
                    
                    break;
                case '*':
                    num1 = Double.parseDouble(memoria.getText().substring(0, memoria.getText().length() - 1));
                    num2 = Double.parseDouble(pantalla.getText());
                    result = num1 * num2;
                    pantalla.setText(String.valueOf(result));
                    
                    break;
                case '/':
                    num1 = Double.parseDouble(memoria.getText().substring(0, memoria.getText().length() - 1));
                    num2 = Double.parseDouble(pantalla.getText());
                    result = num1 / num2;
                    pantalla.setText(String.valueOf(result));
                    
                    break;
                    
                default:
                    
                    
            }
        }
        
    }//GEN-LAST:event_opIgualActionPerformed

